M@SoftwareDecoder		Macro
        MOVAR         R@SaveAcc		;2009-12-03 yf
        MOVRA         STATUSCPU
        MOVAR         R@SaveSTATUSCPU  
        MOVRA         TREG0
        MOVAR         R@IntSaveTreg0
        MOVRA         TREG1
        MOVAR         R@IntSaveTreg1
        MOVRA         TREG2
        MOVAR         R@IntSaveTreg2
    if      E@TregWidthBit > 0xC         
        MOVRA         TREG3
        MOVAR         R@IntSaveTreg3
    endif      
      
    if      E@TregWidthBit > 0x10                
        MOVRA         TREG4
        MOVAR         R@IntSaveTreg4
    endif        

        MOVRA	      R@PauseV
        SAEQI	      0X0
        JMP	      @@@ROM_decode_int_back		;Pause software decode voice

  	if    D@VSDPCM==0x1  
  	
        MOVRA	      R@VDataFormat      
        SAEQI         0x01
        JMP    	      @@@Isnt_PCM_mode
         	
        M@VSDPCM_IntEvent
        
        JMP         @@@ROM_decode_int_back			;PCM software decode
  	endif      

@@@Isnt_PCM_mode:  
                                                    ;2009-12-03 yf
        M@VSD_IntEvent  

@@@ROM_decode_int_back:  	
  	
ENDM



;##################################################################
M@RestoreRegData		Macro				
        MOVRA         R@IntSaveTreg0
        MOVAR         TREG0
        MOVRA         R@IntSaveTreg1
        MOVAR         TREG1
        MOVRA         R@IntSaveTreg2
        MOVAR         TREG2
    if      E@TregWidthBit > 0xC                 
        MOVRA         R@IntSaveTreg3
        MOVAR         TREG3
    endif        
    if      E@TregWidthBit > 0x10                
        MOVRA         R@IntSaveTreg4    
        MOVAR         TREG4
    endif      
    
        MOVRA         R@SaveSTATUSCPU			        ;2009-12-03 yf
        MOVAR         STATUSCPU
        MOVRA         R@SaveAcc               
        
ENDM



